import styled from "styled-components";
import { Project } from "../../types/project";

const All = ({ projectData }: { projectData: Project[] }) => {
  return (
    <Wrap>
      {projectData.map((project) => (
        <Content key={project.id}>
          <div>
            <div>
              <Title>
                <h2>{project.title}</h2>
                <Link>
                  <a
                    href={project.link}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    배포 링크
                  </a>
                </Link>
              </Title>
            </div>
            <span>{project.date}</span>
            <span>
              <p>기간</p> {project.term}
            </span>

            <span>
              <p>Content</p> {project.content}
            </span>
            <span>
              <p>Review</p> {project.review}
            </span>
            <span>
              <p>Role</p> {project.role.join(", ")}
            </span>
            <span>
              <p>Skill</p> {project.skill.join(", ")}
            </span>
          </div>
          <Git>
            <a href={project.git} target="_blank" rel="noopener noreferrer">
              git repository
            </a>
          </Git>
        </Content>
      ))}
    </Wrap>
  );
};

export default All;

const Wrap = styled.div`
  display: flex;
  width: 100%;
  height: 100%;
  justify-content: space-evenly;
  align-items: center;
  a {
    text-decoration: none;
    color: #1f485e;
  }
`;
const Content = styled.div``;
const Title = styled.div`
  display: flex;
  align-items: flex-end;
  h2 {
    font-size: 23px;
    font-weight: bold;
    margin-right: 5px;
  }
`;
const Link = styled.span`
  font-size: 18px;
  padding: 0px 3px;
  background-color: #e4a8a8;
  font-weight: 600;
`;
const Git = styled.span``;
