import { useEffect, useRef } from "react";
import styled from "styled-components";

const List = () => {
    const scrollContainerRef = useRef<HTMLUListElement>(null);

    useEffect(() => {
      const handleScroll = () => {
        // 스크롤 위치에 따라 원하는 작업 수행
        // 예시: 스크롤 위치 로깅
        if (scrollContainerRef.current) {
          console.log("Scroll position:", scrollContainerRef.current.scrollLeft);
        }
      };
  
      if (scrollContainerRef.current) {
        scrollContainerRef.current.addEventListener("scroll", handleScroll);
      }
  
      return () => {
        if (scrollContainerRef.current) {
          scrollContainerRef.current.removeEventListener("scroll", handleScroll);
        }
      };
    }, []);


  return (
    <Lists ref={scrollContainerRef}>
        <Content>ddd1</Content>
        <Content>ddd2</Content>
        <Content>ddd3</Content>
        <Content>ddd4</Content>
        <Content>ddd5</Content>
        <Content>ddd6</Content>
    </Lists>
  )}

  
export default List;

const Lists = styled.ul`
    position: absolute;
    height: 100%;
    display: flex;
    overflow-x: auto;
    overflow-y: hidden;
    white-space: nowrap;
`
const Content = styled.li`
    width: 100vw;
    height: 100%;
`
