import styled from "styled-components";
import { Project } from "../../types/project";
import { AiFillGithub } from "react-icons/ai";

const All = ({ projectData }: { projectData: Project[] }) => {
  return (
    <Wrap>
      {projectData.map((project) => (
        <Content key={project.id}>
          <div>
            <div>
              <Title>
                <h2>{project.title}</h2>
                <Link>
                  <a
                    href={project.link}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    배포 링크
                  </a>
                </Link>

                <a href={project.git} target="_blank" rel="noopener noreferrer">
                  <AiFillGithub />
                </a>
              </Title>
            </div>
            <Duration>
              <span>{project.date}</span>
              <span>
                <p>기간</p> {project.term}
              </span>
            </Duration>

            <span>
              <p>Content</p> {project.content}
            </span>
            <span>
              <p>Review</p> {project.review}
            </span>
            <span>
              <p>Role</p> {project.role.join(", ")}
            </span>
            <span>
              <p>Skill</p> {project.skill.join(", ")}
            </span>
          </div>
        </Content>
      ))}
    </Wrap>
  );
};

export default All;

const Wrap = styled.div`
  display: flex;
  width: 100%;
  height: 100%;
  justify-content: space-evenly;
  align-items: center;

  span,
  p {
    color: black;
  }

  a {
    text-decoration: none;
    color: #1f485e;
  }
`;
const Content = styled.div`
  background-color: white;
  padding: 20px;
`;
const Title = styled.div`
  display: flex;
  align-items: center;
  h2 {
    font-size: 24px;
    margin-right: 10px;
    font-weight: 600;
    color: #1f485e;
  }
`;
const Link = styled.span`
  font-size: 18px;
  margin-right: 7px;
  padding: 0px 3px;
  background-color: #e4a8a8;
  font-weight: 600;
`;

const Duration = styled.div``;
