import styled from "styled-components";

const About = () => {
  return (
    <Wrap>
      <Up>
        <Round>
          <Circle>
          <svg viewBox="0 0 500 500">
<defs>
	<path d="M50,250c0-110.5,89.5-200,200-200s200,89.5,200,200s-89.5,200-200,200S50,360.5,50,250" id="textcircle_top">
		<animateTransform attributeName="transform" begin="0s" dur="20s" type="rotate" from="0 250 250" to="360 250 250" repeatCount="indefinite" />
	</path>
  <path d="M50,250c0-110.5,89.5-200,200-200s200,89.5,200,200s-89.5,200-200,200S50,360.5,50,250" id="textcircle_bottom">
		<animateTransform attributeName="transform" begin="0s" dur="20s" type="rotate" from="195 250 250" to="555 250 250" repeatCount="indefinite" />
	</path>
</defs>
	<text dy="70" textLength="1220">
    
		<textPath >Sweet ~ Coding</textPath>
    <textPath >Since ~ 1999</textPath>
	</text>
</svg>
          </Circle>
        </Round>
        <Outline>
          <Title>About</Title>
          <Line />
          <Info>어쩌구</Info>
          <Info>어쩌구</Info>
          <Info>어쩌구</Info>
        </Outline>
      </Up>
    </Wrap>
  );
};

export default About;

const Wrap = styled.main`
  width: 100%;
  height: 100vh;
  display: flex;
  overflow: hidden;
`;

const Up = styled.section`
  width: 100%;
  height: 45%;
  display: flex;
`;
const Round = styled.article`
  width: 50%;
  height: 100%;
`;

const Circle = styled.div`
  width: 400px;
  height: 400px;
  shape-outside: path(circle(50% at 50% 50%));
  clip-path: path(circle(50% at 50% 50%));
  overflow: hidden;
  position: relative;
`;
const Text = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%) rotate(-90deg);
  white-space: nowrap;
`;

const Outline = styled.article`
  width: 50%;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding-left: 50px;
`;
const Title = styled.span`
  font-size: 90px;
  color: #1f485e;
  font-weight: bold;
  -webkit-text-stroke: 4px white;
  font-family: sans-serif;
  padding-left: 20px;
  letter-spacing: 3px;
`;
const Line = styled.div`
  width: 100%;
  height: 10px;
  margin-bottom: 15px;
  border-bottom: 5px solid white;
`;
const Info = styled.span`
  padding-left: 20px;
  font-size: 22px;
`;
